require("commas") require("serialize") require("tprint") require("var") require("wait") local const = require("fk_const")
-------------------------------------------------- -- Core --------------------------------------------------
Core = {}

--------------------------------------------------
-- Utility
--------------------------------------------------


Utility = {}


function Utility.initialize()
    local initializers = {
    }
    for _, initializer in ipairs(initializers) do
        initializer()
    end
end


function Utility.deinitialize()
end

--------------------------------------------------
-- Plugin Callbacks
--------------------------------------------------


function OnPluginInstall()
    Utility.initialize()
end


function OnPluginEnable()
    OnPluginInstall()
end


function OnPluginClose()
    Utility.deinitialize()
end


function OnPluginDisable()
    OnPluginClose()
end


function OnPluginScreendraw(type, log, line)
end


function OnPluginBroadcast(msg, id, name, text)
end
