muclient.script:
--------------------------------------------------
-- Utility
--------------------------------------------------


Utility = {}


function Utility.initialize()
    local initializers = {
        Config.initialize,
    }
    for _, initializer in ipairs(initializers) do
        initializer()
    end
end


function Utility.deinitialize()
end


function Utility.msg_major(str)
    local msg = "[{B0}Soundpack{70}]: " .. str
    fk_note(msg)
end


function Utility.msg_minor(str)
    local msg = "             " .. str
    fk_note(msg)
end
